var	crypto = require('crypto')
	,	salt = 'poopRadish'
	, fs = require('fs')
	, personDB = require('./personDB.js')
	,	emailSpice = 'redtickcoonhound' // salt for creating objects ID
;

var person = function(obj){
	
	var hash1 = crypto.createHmac('sha1', salt)
		,	hash2 = crypto.createHmac('sha1', emailSpice)
		, t = new Date().getTime()
	;
	
	this._id = hash2.update(obj.email).digest('base64')
	this.password = hash1.update(obj.password).digest('base64')
	this.salt = salt
	this.email = obj.email
	this.fname = 'Sans'
	this.lname = 'Nombre'
	this.location = null
	this.files = null
	this.age = ""
	this.dob = ""
	this.companies = undefined
	this.channels = null
	this.networks = null
	this.comments = undefined
	this.published = undefined
	this.comments = undefined
	this.date_created = t
	this.date_last_edited = t
};

module.exports = person;
